// Menu.js

import React, { useState } from 'react';
import './menuStyles.css'; // Import the external CSS file

const Menu = () => {
  const [isOpen, setIsOpen] = useState(false);
  const [selectedItem, setSelectedItem] = useState('Home');

  const toggleMenu = () => {
    setIsOpen(!isOpen);
  };

  const handleItemClick = (item) => {
    setIsOpen(false);
    setSelectedItem(item);
  };

  return (
    <div className="menuContainer">
      <div className="menuIcon" onClick={toggleMenu}>
        Menu
      </div>
      <p>Assistant PhD Roxana Hala»õi</p>
      <nav className="navigation" isOpen={isOpen}>
        <ul>
          <li>
            <a
              href="/"
              onClick={() => handleItemClick('Home')}
              className={selectedItem === 'Home' ? 'selected' : ''}
            >
              Home
            </a>
          </li>
          <li>
            <a
              href="/personal-info"
              onClick={() => handleItemClick('About Me')}
              className={selectedItem === 'About Me' ? 'selected' : ''}
            >
              About Me
            </a>
          </li>
          <li>
            <a
              href="/teaching"
              onClick={() => handleItemClick('Teaching')}
              className={selectedItem === 'Teaching' ? 'selected' : ''}
            >
              Teaching
            </a>
          </li>
          <li>
            <a
              href="/contact"
              onClick={() => handleItemClick('Contact')}
              className={selectedItem === 'Contact' ? 'selected' : ''}
            >
              Contact
            </a>
          </li>
        </ul>
      </nav>
    </div>
  );
};

export default Menu;
