const MenuContainer = styled.div`
  background-color: #5A1647; /* Updated background color */
  height: 40px;
  padding: 10px;
  display: flex;
  align-items: center;
  justify-content: space-between;

  @media (max-width: 768px) {
    flex-direction: column;
    align-items: flex-start;
    padding: 10px 10px 60px; /* Adjust padding to accommodate menu options */
  }
`;

const Menu = () => {
  const [isOpen, setIsOpen] = useState(false);
  const [selectedItem, setSelectedItem] = useState('Home');

  const toggleMenu = () => {
    setIsOpen(!isOpen);
  };

  const handleItemClick = (item) => {
    setIsOpen(false);
    setSelectedItem(item);
  };

  return (
    <MenuContainer>
      <MenuIcon onClick={toggleMenu}>Menu</MenuIcon>
      <p style={{ color: 'white', fontWeight: '700', fontSize: 18, display: isOpen ? 'none' : 'block' }}>
        Assistant PhD Roxana Hala»õi
      </p>
      <Navigation isOpen={isOpen}>
        <ul>
          <li>
            <Link
              to="/"
              onClick={() => handleItemClick('Home')}
              className={selectedItem === 'Home' ? 'selected' : ''}
            >
              Home
            </Link>
          </li>
          <li>
            <Link
              to="/personal-info"
              onClick={() => handleItemClick('About Me')}
              className={selectedItem === 'About Me' ? 'selected' : ''}
            >
              About Me
            </Link>
          </li>
          <li>
            <Link
              to="/teaching"
              onClick={() => handleItemClick('Teaching')}
              className={selectedItem === 'Teaching' ? 'selected' : ''}
            >
              Teaching
            </Link>
          </li>
          <li>
            <Link
              to="/contact"
              onClick={() => handleItemClick('Contact')}
              className={selectedItem === 'Contact' ? 'selected' : ''}
            >
              Contact
            </Link>
          </li>
        </ul>
      </Navigation>
    </MenuContainer>
  );
};
