import React, {useState} from 'react';
import EducationIcon from './assets/education.svg'
import HobbyIcon from './assets/hobby.svg'
import TeachingIcon from './assets/teaching.svg'
import Bullet from './assets/BulletPoints.svg';
import './Happiness.css'

const Happiness = () => {
    const imageArray = Array.from({ length: 12 }, (_, index) => `./../petPictures/${index + 1}.jpeg`);

    const [selectedImage, setSelectedImage] = useState(null);

    const handleImageClick = (image) => {
        setSelectedImage(image);
    };

    return (
        <div className='HPageContainer'>
            <div className="image-grid">
                {imageArray.map((image, index) => (
                    <img
                        key={index}
                        src={require(`./petPictures/${image}`)}
                        alt={`Image ${index + 1}`}
                        onClick={() => handleImageClick(image)}
                        className="grid-item"
                    />
                ))}
            </div>

            {selectedImage && (
                <div className="modal">
                    <img
                        src={require(`./../petPictures/${selectedImage}`)}
                        alt={`Selected Image`}
                        className="full-size-image"
                    />
                    <button onClick={() => setSelectedImage(null)}>Close</button>
                </div>
            )}
        </div>
    );
};

export default Happiness;
